import { ICommonResponse } from "./common.types";
export interface Skill {
    skillId: string;
    parentSkillId?: string;
    name: string;
    description?: string;
    entityId: string;
    isEnabled: boolean;
    createdAt: Date;
}
export interface IGetAllSkills {
    entityId?: string;
}
export interface IGetAllSkillsResponse extends ICommonResponse {
    data: {
        skillId: string;
        parentSkillId?: string;
        name: string;
        description?: string;
    };
}
export interface IGetSkillDetails {
    skillId: string;
}
export interface IGetSkillDetailsResponse extends ICommonResponse {
    data: Skill;
}
export interface IAddNewSkill {
    entityId?: string;
    name: string;
    description: string;
}
export interface IAddNewSkillResponse extends ICommonResponse {
    skillId: string;
}
export interface IAddMultipleNewSkills {
    entityId?: string;
    skills: {
        name: string;
        description: string;
    }[];
}
export interface IAddMultipleNewSkillsResponse extends ICommonResponse {
    skillIds: string[];
}
